import React, {useEffect, useState} from "react"
import {useRouter} from "next/router";
import axios from "axios";
import Loading from "../../components/Loading";
import Error from "../../components/Error";
import RatesTable from "../../components/RatesTable";
import Select from "../../components/form/Select";
import {Col, Row, Container} from "react-bootstrap";
import DatePicker from "react-datepicker";


const getDateString = date => {
    const y = date.getFullYear();
    const m = date.getMonth() + 1;
    const d = date.getDate();
    return y + "-" + m + "-" + d;
}

const Base = (props) => {
    const {base, date, rates} = props.data;

    const router = useRouter();

    const handleBaseChange = e => {
        router.push(`/${date}/${e.target.value}`)
    }

    const handleDateChange = date => {

        router.push(`/${getDateString(date)}/${base}`)
    }

    if(router.isFallback) return <Loading />;

    return (
        <Container>
            <Row className="bg-light p-4" style={{ margin: "0 0px" }}>
                <Col>
                    <Select handleChange={handleBaseChange} base={base} options={Object.keys(rates)} />
                </Col>
                <Col>
                    <DatePicker selected={new Date(date)} className="form-control" onChange={date => handleDateChange(date)} />
                </Col>
            </Row>
            <Row>
                <Col>
                    <RatesTable rates={rates} />
                </Col>
            </Row>
        </Container>
    )
}

export async function getStaticPaths() {
    return {
        paths: [
            { params: { base: "EUR", date: 'latest',  } },
            { params: { base: "EUR", date: getDateString(new Date()),  } },
        ],
        fallback: true
    }
}

export async function getStaticProps (context) {
    const {date, base} = context.params;
    const result = await axios.get(`https://api.exchangeratesapi.io/${date}?base=${base}`)
    const data = await result.data;
    return {
        props: {data},
    }
}
export default Base;
