                </Col>
                <Col>
                    <DatePicker selected={dateState} className="form-control" onChange={date => changeDateAndReFetchData(date)} />
                </Col>
            </Row>
            <Row>
                <Col>
                    <RatesTable rates={rates} />
                </Col>
            </Row>
        </Container>
    )
}

export async function getStaticPaths() {
    return {
        paths: [
            { params: { base: "EUR", date: 'latest',  } },
        ],
        fallback: false
    }
}

export async function getStaticProps (context) {
    const {date, base} = context.params;
    const result = await axios.get(`https://api.exchangeratesapi.io/${date}?base=${base}`)
    const data = await result.data;
    return {
        props: {data},
    }
}
export default Base;
