import React, {Component} from "react";
import {loadCurrencyRates} from "../store/actions/actions";
import {connect} from "react-redux";
import Error from "../Components/Error/Error";

class Index extends Component {
    componentDidMount() {
        const {loadCurrencyRates} = this.props;
        loadCurrencyRates();
    }
    render = () => {
        const {isLoading, error, rate} = this.props.state;

        if(isLoading) return <i className="fas fa-circle-notch fa-spin fa-2x"></i>;
        if(error) return <Error errorMsg={error} />
        return (
            <div>Rates = {rate}</div>
        )
    }
}

const state = state => ({
    state
})

const mapDispatchToProps = {
    loadCurrencyRates
}

export default connect(state, mapDispatchToProps)(Index);
