import React, {Component, useState, useEffect} from "react";
import axios from "axios";
import Layout from "../components/Layout";
import {useRouter} from "next/router";
import Loading from "../components/Loading";
import Error from "../components/Error";


const Index = (props) => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [base, setBase] = useState("");
    const [date, setDate] = useState("");
    const [rates, setRates] = useState([]);

    const router = useRouter();
    console.log(rates);

    useEffect( () => {
        const fetchData = async() => {
            try {
                setIsLoading(true);
                const result = await axios.get("https://api.exchangeratesapi.io/latest?base=USD");
                const {data} = result;
                setBase(data.base);
                setDate(data.date);
                setRates(data.rates);
                setIsLoading(false);
            } catch (error) {
                setError("Couldn't load data. Try again later or contact a developer if the issue persists.");
            }
        }
        fetchData();


    }, [])

    if(isLoading) {
        return <Loading />;
    }
    if(error) {
        return <Error errorMsg="We've encountered an error" />;
    }
    return (
        <div>
            <h1>{base} - {date}</h1>
            <p>Hey</p>
        </div>
    )
}
export default Index;
