import React, {useEffect, useState} from "react"
import {useRouter} from "next/router";
import axios from "axios";
import Loading from "../../components/Loading";
import Error from "../../components/Error";
import RatesTable from "../../components/RatesTable";
import Select from "../../components/form/Select";
import {Col, Row, Container} from "react-bootstrap";
import DatePicker from "react-datepicker";



const Base = (props) => {
    const {base, date, rates} = props.data;

    const router = useRouter();
    const handleBaseChange = e => {
        router.push(`/`)
    }

    return (
        <Container>
            <Row className="bg-light p-4" style={{ margin: "0 0px" }}>
                <Col>
                    <Select handleChange={handleBaseChange} base={baseState} options={Object.keys(rates)} />
                </Col>
                <Col>
                    <DatePicker selected={dateState} className="form-control" onChange={date => changeDateAndReFetchData(date)} />
                </Col>
            </Row>
            <Row>
                <Col>
                    <RatesTable rates={rates} />
                </Col>
            </Row>
        </Container>
    )
}

export async function getStaticPaths() {
    return {
        paths: [
            { params: { base: "EUR", date: 'latest',  } },
        ],
        fallback: false
    }
}

export async function getStaticProps (context) {
    const {date, base} = context.params;
    const result = await axios.get(`https://api.exchangeratesapi.io/${date}?base=${base}`)
    const data = await result.data;
    return {
        props: {data},
    }
}
export default Base;
