import React, {useEffect, useState} from "react"
import {useRouter} from "next/router";
import axios from "axios";
import Loading from "../../components/Loading";
import Error from "../../components/Error";
import RatesTable from "../../components/RatesTable";
import Select from "../../components/form/Select";
import {Col, Row, Container} from "react-bootstrap";

const Base = () => {
    /*
     * Setting the initial state values
     */
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [baseState, setBaseState] = useState("");
    const [dateState, setDateState] = useState("latest");
    const [rates, setRates] = useState([]);

    /*
     * Fetching data from the url
     */
    const router = useRouter();
    const {date, base} = router.query;

    /*
     * Fetching the data with our information
     */
    useEffect( () => {
        async function fetchData() {
            try {
                setIsLoading(true);
                const result = await axios.get("https://api.exchangeratesapi.io/" + dateState + "?base=" + baseState);
                const {data} = result;
                setBaseState(data.base);setDateState(data.date);setRates(data.rates);setIsLoading(false);
            } catch (error) {
                setIsLoading(false);
                setError("Couldn't load data. Try again later or contact a developer if the issue persists.");
            }
        }
        fetchData();
    }, [date, base])

    if(isLoading) {
        return <Loading />;
    }
    if(error) {
        return <Error errorMsg="We've encountered an error" />;
    }

    const handleBaseChange = e => {
        changeBaseAndReFetchData(e.target.value);
    }
    const changeBaseAndReFetchData = base => {
        setBaseState(base);

        const url = `/${dateState}/${base}`;
        router.push(url)
    }

    return (
        <Container>
            <Row className="bg-light">
                <Col md={6}>
                    <Select handleChange={handleBaseChange} base={baseState} options={Object.keys(rates)} />
                </Col>
                <Col md={6}>
                    Test
                </Col>
            </Row>
            <RatesTable rates={rates} />
        </Container>
    )
}
export default Base;
