import React, {useEffect, useState} from "react"
import {useRouter} from "next/router";
import axios from "axios";
import Loading from "../../components/Loading";
import Error from "../../components/Error";
import RatesTable from "../../components/RatesTable";
import Select from "../../components/form/Select";
import {Col, Row, Container} from "react-bootstrap";
import DatePicker from "react-datepicker";

const Base = () => {
    /*
     * Fetching data from the url
     */
    const router = useRouter();
    const {date, base} = router.query;

    /*
    * Setting the initial state values
    */
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [baseState, setBaseState] = useState("");
    const [dateState, setDateState] = useState(null);
    const [rates, setRates] = useState([]);

    /*
     * Fetching the data with our information
     */
    useEffect( () => {
        async function fetchData() {

            try {
                setIsLoading(true);
                const result = await axios.get("https://api.exchangeratesapi.io/" + dateToString(dateState) + "?base=" + baseState);
                const {data} = result;
                alert("https://api.exchangeratesapi.io/" + dateToString(dateState) + "?base=" + baseState)
                let receivedDateObject;
                if(data.date === "latest") {
                    receivedDateObject = new Date()
                } else {
                    receivedDateObject = new Date(data.date)
                }


                setBaseState(data.base);setDateState(receivedDateObject);setRates(data.rates);setIsLoading(false);
            } catch (error) {
                setIsLoading(false);
                setError("Couldn't load data. Try again later or contact a developer if the issue persists.");
            }
        }
        setDateState(date === "latest" ? new Date() : new Date(date))
        fetchData();
    }, [date, base])

    if(isLoading) {
        return <Loading />;
    }
    if(error) {
        return <Error errorMsg={error} />;
    }

    const handleBaseChange = e => {
        changeBaseAndReFetchData(e.target.value);
    }
    const changeBaseAndReFetchData = base => {
        setBaseState(base);

        const url = `/${dateToString(dateState)}/${base}`;
        router.push(url)
    }

    const changeDateAndReFetchData = date => {
        setDateState(date)
        console.log("NEW DATE BETREFT", dateState)
        const url = `/${dateToString(date)}/${baseState}`;
        router.push(url)
    }
    const dateToString = date => date.getFullYear() + "-" + (date.getMonth() + 1) + "-" + date.getDate();
    return (
        <Container>
            <Row className="bg-light p-4" style={{ margin: "0 0px" }}>
                <Col>
                    <Select handleChange={handleBaseChange} base={baseState} options={Object.keys(rates)} />
                </Col>
                <Col>
                    <DatePicker selected={dateState} className="form-control" onChange={date => changeDateAndReFetchData(date)} />
                </Col>
            </Row>
            <Row>
                <Col>
                    <RatesTable rates={rates} />
                </Col>
            </Row>
        </Container>
    )
}
export default Base;
